// backend/services/tekmetricService.js

const axios = require('../utils/axiosConfig');

async function listRepairOrders() {
    try {
        const response = await axios.get('/repair-orders', {
            params: { shop: 238 }
        });
        return response.data;
    } catch (error) {
        console.error('Error listing repair orders:', error.message);
        throw error;
    }
}

async function updateRepairOrder(details) {
    try {
        const [id,...rest] = details.split(' with ');
        const data = JSON.parse(rest.join(' '));
        const response = await axios.patch(`/repair-orders/${id}`, data);
        return response.data;
    } catch (error) {
        console.error('Error updating repair order:', error.message);
        throw error;
    }
}

async function listAppointments() {
    try {
        const response = await axios.get('/appointments', {
            params: { shop: 238 }
        });
        return response.data;
    } catch (error) {
        console.error('Error listing appointments:', error.message);
        throw error;
    }
}

async function listVehicles() {
    try {
        const response = await axios.get('/vehicles', {
            params: {
                shop: 238,
                updatedDateStart: '2024-05-02T00:00:00Z',
                updatedDateEnd: '2024-05-02T23:59:59Z'
            }
        });
        return response.data;
    } catch (error) {
        console.error('Error listing vehicles:', error.message);
        throw error;
    }
}

module.exports = {
    listRepairOrders,
    updateRepairOrder,
    listAppointments,
    listVehicles,
};
